import React, { Component } from 'react'

export class ChatContainer extends Component {
  constructor(props) {
    super(props)

    this.state = {
      message: "",
      messages: [],
    }
  }

  componentDidMount = () => {
    this.props.socket.on('chat',  (obj) => {
      const log = this.state.messages
      obj.key = 'key_' + (this.state.messages.length + 1)
      log.push(obj)
      this.setState({messages: log})
    })
  }

  handleChange = e => {
    e.preventDefault();
    const msg = e.target.value;
    this.setState({
      message: msg,
    })
  }

  handleSubmit = e => {
    if(this.state.message == "") {
      return
    }

    this.props.socket.emit('chat', {
      name: this.props.name, 
      msg: this.state.message,
      timestamp: this.getTimeStamp()
    });

    this.setState({
      message: ""
    })
  }

  getTimeStamp = () => {
    var date = new Date()
    var formatedDate = 
      this.changeFormat(date.getFullYear(), 4) + '-' +
      this.changeFormat(date.getMonth() + 1, 2) + '-' +
      this.changeFormat(date.getDate(), 2) + ' ' +
  
      this.changeFormat(date.getHours(), 2) + ':' +
      this.changeFormat(date.getMinutes(), 2) + ':' +
      this.changeFormat(date.getSeconds(), 2);
  
    return formatedDate;
  }

   changeFormat = (n, digits) => {
    var zero = '';
    var i;
    n = n.toString();

    if (n.length < digits) {
      for (i=0; i<digits-n.length; i++)
        zero += '0';
    }
    return zero + n;
  }

  handleKeyPress = event => {
    const msg = this.state.message;
    if(msg != "" && event.key == 'Enter') {
      this.handleSubmit()
    }
  }

  scrollToBottom = () => {
    const chat = document.getElementById("chatLog");
    chat.scrollTop = chat.scrollHeight;
  }

  render() {
    const messages = this.state.messages.map(e => (
      <div key={e.key}>
        <span>{e.name}&nbsp;</span>
        <span>{e.msg}&nbsp;</span>
        <span>{e.timestamp}&nbsp;</span>
      </div>
    ))
    return (
      <div>
        <div className="col">
          <div className="row-xl-10">
            {messages}
          </div>
          <hr/>
          <div className="row-xl-2">
            <div className="input-group">
              <input className="form-control" 
                    placeholder="Message..." 
                    name="chat"
                    type="message" 
                    value={this.state.message} 
                    onChange={this.handleChange}
                    onKeyPress={this.handleKeyPress} />
              <div className="input-group-btn">
                <button className="btn btn-default" 
                        type="submit"
                        onClick={this.handleSubmit} 
                        onSubmit={this.handleSubmit}>
                  <i className="fa fa-send-o"/>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default ChatContainer
