import React, { Component } from "react";
import "./css/Account.scss";

//const connection = require('../../../server.js')
// 유진
var http = require('http');
http.createServer(function (req, res) {
  res.writeHead(200, {'Content-Type': 'text/plain'});
  res.write('Hello World!');
  res.end();
}).listen(8080);

console.log('Signup.js');

// 유진
const express = require('express');
const app = express();
var router = express.Router();
const path = require('path');
var mysql = require('mysql')
// 여기까지

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/    // 이메일 정규 표현식 [아이디]@[###.###]
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: null,
      email: null,
      password: null,
      formErrors: {
        name: "",
        email: "",
        password: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    
    if (formValid(this.state)) {
      console.log(`                              
        —SUBMITTING—
        Name : ${this.state.name}
        Email : ${this.state.email}
        Password : ${this.state.password}
      `);

      var connection = mysql.createConnection({
        host: "buzz-database.cj4klynaikoy.ap-northeast-2.rds.amazonaws.com",
        user: "Buzz",
        password : "buzz1234",
        port : 3306,
        database : "innodb"
     });
     
     connection.connect();

      router.get('/', function(req, res) {
        res.sendFile(path.join(__dirname, '.\Signup.js'))
      })
      
      router.post('/', function(req, res) {
         var UserName = req.body.id;
         var Email = req.body.email;
         var Password = req.body.pw;
      
         if(!UserName || !Email || !Password) {
            connection.query("INSERT INTO User (UserName, Email, Password) VALUES ('"+ UserName + "', '" + Email + "', '" + Password + "')", function(error, result, fields) {
               if (error) {
                  res.send("err : " + error)
               }
               else {
                  console.log("Success")
                  console.log(Email + ', ' + Password)
                  res.send("Success " + Email + ", " + Password)
               }
            })
         }
      })
      
      module.exports = router;

      //db에 유저 정보 추가 부분 
     /* router.post('/', async(req, res, next) => {

        try{
          let insertUserQuery = 'insert into innodb.User(UserName, Email, Password)  values (?,?,?)';
          let insertResult = await db.queryParamCnt_Arr(insertUserQuery,[this.state.name, this.state.email, this.state.password]);
  
          if(!insertResult){
            console.log("Insert Error");
            return next(500);
          }
  
          res.status(201).send({
            "message" : "insert new user success"
          });
        } catch(err){
          return next(err);
        }
      });*/

      //module.exports = router;
      
    } 
  };

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "name":
        formErrors.name =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "invalid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 6 ? "minimum 6 characaters required" : "";
        break;
      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { formErrors } = this.state;

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1>Create Account</h1>
          <form onSubmit={this.handleSubmit} method="post" noValidate>
            <div className="name">
              <label htmlFor="name">Name</label>
              <input
                className={formErrors.name.length > 0 ? "error" : null}
                placeholder="Name"
                type="name"
                name="name"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.name.length > 0 && (
                <span className="errorMessage">{formErrors.name}</span>
              )}
            </div>
            
            <div className="email">
              <label htmlFor="email">Email</label>
              <input
                className={formErrors.email.length > 0 ? "error" : null}
                placeholder="Email"
                type="email"
                name="email"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.email.length > 0 && (
                <span className="errorMessage">{formErrors.email}</span>
              )}
            </div>

            <div className="password">
              <label htmlFor="password">Password</label>
              <input
                className={formErrors.password.length > 0 ? "error" : null}
                placeholder="Password"
                type="password"
                name="password"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.password.length > 0 && (
                <span className="errorMessage">{formErrors.password}</span>
              )}
            </div>
            <div className="createAccount">
              <button type="submit">Create Account</button>
              <div>
                <small>
                  <a href="/login">Already Have an account?</a>
                </small>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default Signup;
