import React, { Component } from 'react'

export class ChatContainer extends Component {
  constructor(props) {
    super(props)

    this.state = {
      name: null,
      message: null,
    }
  }

  componentDidMount = () => {
    this.setState({
      name: this.props.name,
    })
  }

  handleChange = e => {
    e.preventDefault();
    const {name, message} = e.target;

  }

  handleSubmit = () => {
    this.props.socket.on('send_msg', this.state.name, this.state.message);
  }

  render() {
    return (
      <div>
        <div className="input-group">
          <input className="form-control" placeholder="Message..." name="chat" id="srch-term"></input>
          <div className="input-group-btn">
            <button className="btn btn-default" type="submit" onSubmit={this.handleSubmit}>
              <i className="fa fa-send-o"/>
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default ChatContainer
