import React, { Component } from 'react'

export class ChatContainer extends Component {
  constructor(props) {
    super(props)

    this.state = {
      message: "",
      chat: [],
    }


    // this.props.socket.on('receive_msg', function(data) {
    //   addMessage(data)
    // })

    // const addMessage = data => {
    //   console.log(data)
    //   this.setState({
    //     chat: [...this.state.chat, data]
    //   })
    //   console.log(this.state.chat)
    // };
  }

  componentDidMount = () => {
    this.props.socket.on('chat_msg', ({name, msg}) => {
      this.setState({
        chat: [...this.state.chat, {name, msg}]
      });
    });
  }

  handleChange = e => {
    e.preventDefault();
    const msg = e.target.value;
    this.setState({
      message: msg,
    })
  }

  handleSubmit = e => {
    this.props.socket.emit('send_msg', {
      user: this.props.name, 
      message: this.state.message
    });

    this.setState({
      message: ""
    })
  }

  handleKeyPress = event => {
    const msg = this.state.message;
    if(msg != "" && event.key == 'Enter') {
      this.handleSubmit()
    }
  }

  scrollToBottom = () => {
    const chat = document.getElementById("chatLog");
    chat.scrollTop = chat.scrollHeight;
  }

  renderChat = () => {
    const { chat } = this.state;
    console.log(chat)
    return chat.map(({user, msg}, idx) => {
      <div key={idx}>
        <span style={{color: "green"}}>{user} : </span>
        <span>{msg}</span>
      </div>
    });
  }
  // showChatValue = () => {
  //   this.state.messages.map(message => {

  //   })
  // }

  render() {
    return (
      <div>
        <div className="col">
          <div className="row-xl-10">
            {this.renderChat()}
          </div>
          <hr/>
          <div className="row-xl-2">
            <div className="input-group">
              <input className="form-control" 
                    placeholder="Message..." 
                    name="chat"
                    type="message" 
                    value={this.state.message} 
                    onChange={this.handleChange}
                    onKeyPress={this.handleKeyPress} />
              <div className="input-group-btn">
                <button className="btn btn-default" type="submit" onClick={this.handleSubmit} onSubmit={this.handleSubmit}>
                  <i className="fa fa-send-o"/>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default ChatContainer
