import React, { Component } from 'react'

export class ChatContainer extends Component {
  constructor(props) {
    super(props)

    this.state = {
      message: "",
    }
  }

  handleChange = e => {
    e.preventDefault();
    const msg = e.target.value;
    this.setState({
      message: msg,
    })
  }

  handleSubmit = e => {
    this.props.socket.on('send_msg', this.props.name, this.state.message);

    e.preventDefault();
  }

  handleKeyPress = event => {
    if(event.key == 'Enter') {
      this.handleSubmit()
    }
  }

  render() {
    return (
      <div>
        <div className="input-group">
          <input className="form-control" 
                 placeholder="Message..." 
                 name="chat"
                 type="message" 
                 value={this.state.message} 
                 onChange={this.handleChange}
                 onKeyPress={this.handleKeyPress} />
          <div className="input-group-btn">
            <button className="btn btn-default" type="submit" onSubmit={this.handleSubmit}>
              <i className="fa fa-send-o"/>
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default ChatContainer
