import React, { Component } from 'react'

export class ChatContainer extends Component {
  constructor(props) {
    super(props)

    this.state = {
      message: "",
      messages: [],
    }

    const addMessage = (data) => {
      console.log(data)
      this.setState({
        messages: [...this.state.messages, data]
      })
      console.log(this.state.messages)
    }

    this.props.socket.on('receive_msg', function(data) {
      addMessage(data)
    })
  }


  handleChange = e => {
    e.preventDefault();
    const msg = e.target.value;
    this.setState({
      message: msg,
    })
  }

  handleSubmit = e => {
    this.props.socket.emit('send_msg', this.props.name, this.state.message);
    this.setState({
      message: ""
    })
  }

  handleKeyPress = event => {
    const msg = this.state.message;
    if(msg != "" && event.key == 'Enter') {
      this.handleSubmit()
    }
  }

  render() {
    return (
      <div>
        <div className="input-group">
          <input className="form-control" 
                 placeholder="Message..." 
                 name="chat"
                 type="message" 
                 value={this.state.message} 
                 onChange={this.handleChange}
                 onKeyPress={this.handleKeyPress} />
          <div className="input-group-btn">
            <button className="btn btn-default" type="submit" onClick={this.handleSubmit} onSubmit={this.handleSubmit}>
              <i className="fa fa-send-o"/>
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default ChatContainer
